import React, { useEffect, useState } from 'react'
import ReactDOM from 'react-dom'

export default function Main(){


    const buttonCoordinates = document.querySelector('[aria-label="My Nifty Button!"]').getBoundingClientRect()

    const [x, setX] = useState(buttonCoordinates.x - 400)
    const [y, setY] = useState((buttonCoordinates.y - 500))
    const [opacity, setOpacity] = useState("100%")


    return  <div style={{
        position: "fixed",
        opacity: opacity,
        width:"300px", 
        height:"450px",
        top: String(y)+"px",
        left: String(x)+"px",
        right:"0px",
        backgroundColor:"ghostwhite",
        boxShadow: `0 5px 5px 5px rgb(119 119 119 / 25%)`,
        zIndex: "99999"}}>
            <div onClick={e => ReactDOM.unmountComponentAtNode(document.getElementById('extension-wrapper'))}>Close</div>
            <div 
                style={{opacity: opacity === "0%"? "10%" : "100%",cursor:"grab" ,position: "absolute", top:"0px",width:"100%", height:"100%",backgroundColor:"#9CA3AF",boxShadow: "0 8px 6px -6px black"}} 
                draggable={true} 
                onDragStart={e=> setOpacity("0%")} 
                onDragEnd={e=> setOpacity("100%")} 
                onDrag={e => {
                    if(e.clientX !== 0 && e.clientY !==0){ 
                        setX(x + (e.clientX - x)); 
                        setY(y + (e.clientY - y))}
                }}>
            </div>
            <iframe id="amplemarketExtension" style={{opacity: opacity, position: "relative", top:"10px", height:"100%", width:"fit-content", margin:"0px", border:"none"}}src="http://localhost:5000/templates"></iframe>
        </div>

}
