import React from 'react'
import ReactDOM from 'react-dom'

import Main from './Main/Main'

const URL = "http://localhost:5000"

async function main(){
    const sdk = await InboxSDK.load(2,"sdk_gmailSnippet_74536b3008")
    let eventListenerAdded = false;
    sdk.Compose.registerComposeViewHandler(function(composeView){
        // a compose view has come into existence, do something with it!
        composeView.addButton({
          title: "My Nifty Button!",
          iconUrl: 'https://example.com/foo.png',
          onClick: function(event) {
            if(!eventListenerAdded){
              window.addEventListener("message", e => {
                if(e.origin === URL){
                  if(e.data.action === "postSnippetToBody"){
                    event.composeView.setBodyText(e.data.snippet)
                  }
                  if(e.data.action === "saveSnippetFromBody"){
                    fetch(URL+"/api/addSnippet", {
                      method: 'POST',
                      mode: "cors",
                      headers: {
                        'Content-Type': 'application/json'
                      },
                      body: JSON.stringify({
                        user: sdk.User.getEmailAddress(),
                        snippet: event.composeView.getHTMLContent()
                      })
                    }).then(res => console.log(res), err=> console.log(err));
                  }

                }})
            }
            eventListenerAdded =true;
            ReactDOM.render(<Main/>,document.body.appendChild(document.createElement('div')))
          },
        });


      })
      ;
}
main().then(res => console.log(res), err => console.log(err))
