const React = require('react')
const ReactDOM = require('react-dom')

const Main = require('./Main')
// (async function main(){
//     console.log("here")
//     const sdk = await InboxSDK.load(2,"sdk_gmailSnippet_74536b3008")
//     console.log(sdk)
// })()

console.group(Main)

async function main(){
    const sdk = await InboxSDK.load(2,"sdk_gmailSnippet_74536b3008")
    sdk.Compose.registerComposeViewHandler(function(composeView){
        // a compose view has come into existence, do something with it!
        composeView.addButton({
          title: "My Nifty Button!",
          iconUrl: 'https://example.com/foo.png',
          onClick: function(event) {
              ReactDOM.render(<Main />,document.body.appendChild(document.createElement('div')))
          },
        });
      });
}
main().then(res => console.log(res), err => console.log(err))
