import React from 'react'
import ReactDOM from 'react-dom'

import Main from './Main/Main'

import { v4 as uuidv4 } from 'uuid';

const URL = "http://localhost:5000"

if(Object.is(document.getElementById('extension-wrapper', null))){
  const wrapper = document.createElement('div')
  wrapper.id = 'extension-wrapper'
  document.body.appendChild(wrapper)
}

async function main(){

    const sdk = await InboxSDK.load(2,"sdk_gmailSnippet_74536b3008")
    let eventListenerAdded = false;
    sdk.Compose.registerComposeViewHandler(function(composeView){
        if(sdk.User.getEmailAddress().includes("@gmail.com")){
          composeView.addButton({
            title: "My Nifty Button!",
            iconUrl: 'https://example.com/foo.png',
            onClick: function(event) {
              if(!eventListenerAdded){
                window.addEventListener("message", e => {
                  if(e.origin === URL){
                    if(e.data.action === "postSnippetToBody"){
                      event.composeView.setBodyText(e.data.snippet)
                    }
                    if(e.data.action === "saveSnippetFromBody"){
                      const snippetBody = event.composeView.getTextContent();
                      const name = prompt("Please enter a name for your snippet")
                      fetch(URL+"/api/addSnippet", {
                        method: 'POST',
                        mode: "cors",
                        headers: {
                          'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                          user: sdk.User.getEmailAddress(),
                          snippet: snippetBody,
                          name: name,
                          id: uuidv4(snippetBody)
                        })
                      })
                      .then(res => {
                        if(res.ok){
                          document.getElementById("amplemarketExtension").contentWindow.postMessage({
                            'action': "fetchContents",
                          }, "*")
                        }
                      })
                      .catch(err=> console.log(err));
                    }

                  }})
              }
              eventListenerAdded =true;
              ReactDOM.render(<Main />,document.getElementById('extension-wrapper'))
            },
          });
          composeView.on('destroy', function(event) {
          });
        }
      })
      ;
}
main().then(res => console.log(res), err => console.log(err))
