import './App.css';

import React from "react";


const templates = [{
  name: "test",
  snippet: "test"
}]

function App() {
  return (
    <div className="bg-gray-50 w-screen h-screen relative">
      <header className="w-full text-center text-base font-semibold pt-4"> Personal Snippets</header>
      <div className="w-full">
        {templates.map(template => {
          return <div className="flex flex-row justify-between px-4">
            <p>{template.name}</p>
              <p onClick={console.log("here")}>{template.snippet}</p>
          </div>
        })}
      <div className="absolute bottom-0 h-1/6 bg-gray-100 w-full border-t-2 border-gray-200">
        <div className="ml-1 h-1/2 flex items-center space-x-2 text-base">
        <svg 
        xmlns="http://www.w3.org/2000/svg" 
        className="h-6 w-6" 
        fill="none" 
        viewBox="0 0 24 24" 
        stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v3m0 0v3m0-3h3m-3 0H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z" />
</svg>
          <p className="">Turn this draft into a snippet</p>
        </div>
        <div className="ml-1 h-1/2 flex items-center space-x-2 text-base">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
        </svg>
          <p className="">Manage snippets</p>

        </div>
      </div>
      </div>



    </div>
  );
}

export default App;
