import './App.css';

import React, {useState, useEffect} from "react";

function Template(props){
  const {name, snippet} = props.template

  function postSnippetToBody(){
    const message = {
      'action': "postSnippetToBody",
      'snippet': snippet
    }
    window.parent.postMessage(message, "*")
  }

  return <div className="flex my-2 flex-col h-auto justify-between px-2 hover:bg-gray-100 cursor-pointer" onClick={postSnippetToBody}>
    <p className="text-gray-700 font-medium  flex self-start text-base capitalize">{name}</p>
    <p className="text-gray-500 mt-1 flex self-center w-full text-sm">{snippet}</p>
</div>
}


const templates = [{
  name: "test",
  snippet: "test"
},{
  name: "test1",
  snippet: "test1test1test1tes t1test1test 1test1test1test1"
},{
  name: "test1",
  snippet: "Hey Joao, I would totally love to buy your great product my great friend bro !"
},]

function App() {
  function saveSnippetFromBody(){
    const message = {
      'action': "saveSnippetFromBody",
    }
    window.parent.postMessage(message, "*")
  }

  useEffect(() => {
    console.log("event listener added")
    document.addEventListener("message", (e) => {
        if(e.origin === "https://mail.google.com"){
            console.log(e.data)
        }
    }), []
})

  return (
    <div className="shadow-inner bg-gray-50 w-screen h-screen relative">
        <div style={{scrollbarWidth: "none"}} className="h-4/6 overflow-y-scroll pt-2 px-1">
        {templates.map(template => {
          return <Template template={{name: template.name, snippet: template.snippet}}/>
        })}
        </div>
      <div className="absolute bottom-0 h-1/5 bg-gray-100 w-full border-t-1 border-gray-200">
        <div className="pl-2 h-1/2 flex items-center space-x-2 text-sm hover:bg-gray-200 active:bg-gray-300" onClick={saveSnippetFromBody}>
          <svg 
          xmlns="http://www.w3.org/2000/svg" 
          className="h-6 w-6" 
          fill="none" 
          viewBox="0 0 24 24" 
          stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M12 9v3m0 0v3m0-3h3m-3 0H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z" />
  </svg>
          <p className="text-gray-800">Turn this draft into a snippet</p>
        </div>
        <div className="pl-2 h-1/2 flex items-center space-x-2 text-sm hover:bg-gray-200 active:bg-gray-300">          
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
          </svg>         
          <p className="text-gray-800">Manage snippets</p>
        </div>
      </div>



    </div>
  );
}

export default App;
